import React, { Fragment, useState } from "react";
import { Routes, Route, Link, useLocation } from "react-router-dom";
import BarChart from "./components/BarChart";
import PieChart from "./components/PieChart";
import PaginationTable from "./components/PaginationTable";
import Statistics from "./components/Statistics";
import "./App.css";

function App() {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedMonth, setSelectedMonth] = useState("");
  const location = useLocation();

  const handleSearchChange = (e) => {
    // Check the active route and update the state accordingly
    if (location.pathname === "/") {
      setSearchQuery(e.target.value);
    }
  };

  const handleMonthChange = (e) => {
    setSelectedMonth(e.target.value);
  };

  const handleClearFilters = () => {
    setSearchQuery("");
    setSelectedMonth("");
    if (location.pathname === "/") {
      navigate("/", { replace: true });
    }
  };



  const handleInputKeyDown = (e) => {
    if (e.key === "Enter") {
      e.target.blur(); // Remove focus on Enter key press
    }
  };

  return (
    <Fragment>
      <div className="navbar">
        <div className="search-dropdown-container">
          {location.pathname === "/" && (
            <input
              type="text"
              placeholder="Search..."
              value={searchQuery}
              onChange={handleSearchChange}
              onKeyDown={handleInputKeyDown}
            />
          )}
          <label htmlFor="monthDropdown" className="select-month-label">
            Select Month:&nbsp;
            <select
              id="monthDropdown"
              value={selectedMonth}
              onChange={handleMonthChange}
            >
              <option value="">All</option>
              <option value="January">January</option>
              <option value="February">February</option>
              <option value="March">March</option>
              <option value="April">April</option>
              <option value="May">May</option>
              <option value="June">June</option>
              <option value="July">July</option>
              <option value="August">August</option>
              <option value="September">September</option>
              <option value="October">October</option>
              <option value="November">November</option>
              <option value="December">December</option>
            </select>
            <button onClick={handleClearFilters}>Clear Filters</button>
          </label>
        </div>

        <nav>
          <div className="slider"></div>

          <Link to="/" className="nav-link">
            <input type="radio" name="slider" id="home" />
            <label htmlFor="home" className="home">
              Transactions
            </label>
          </Link>
          <Link to="/barchart" className="nav-link">
            <input type="radio" name="slider" id="blog" />
            <label htmlFor="blog" className="blog">
              barchart
            </label>
          </Link>
          <Link to="/piechart" className="nav-link">
            <input type="radio" name="slider" id="code" />
            <label htmlFor="code" className="code">
              piechart
            </label>
          </Link>
          <Link to="/statistics" className="nav-link">
            <input type="radio" name="slider" id="help" />
            <label htmlFor="help" className="help">
              Stats
            </label>
          </Link>
        </nav>
      </div>

      <Routes>
        <Route
          path="/"
          element={
            <PaginationTable
              searchQuery={searchQuery}
              selectedMonth={selectedMonth}
            />
          }
        />
        <Route
          path="/barchart"
          element={<BarChart selectedMonth={selectedMonth} />}
        />
        <Route
          path="/piechart"
          element={<PieChart selectedMonth={selectedMonth} />}
        />
        <Route
          path="/statistics"
          element={<Statistics selectedMonth={selectedMonth} />}
        />
      </Routes>
    </Fragment>
  );
}

export default App;
