import React, { useEffect, useState,useMemo } from 'react';
import axios from 'axios';
import {useTable} from 'react-table'
import {columns} from './columns'

const BasicTable = () => {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/product/productTransactions', {
          params: {
            page: 1,
            limit: 10,
            search: 'November',
          },
        });

        setTransactions(response.data.transactions);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchData();
  }, []); 

  const columns=useMemo(()=>columns,[])
  const data=useMemo(()=>transactions,[])

 const tableInstance= useTable({
    columns:columns,
    data:transactions
  })

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  

    return (
        <div>
      <h1>Product Transactions</h1>
      <ul>
        {transactions.map((transaction) => (
          <li key={transaction.id}>
            {/* Render your transaction data here */}
            {transaction.title} - {transaction.price}
          </li>
        ))}
      </ul>
    </div>
    );
}

export default BasicTable;
