import React, { useEffect, useState } from 'react';
import { Bar } from 'react-chartjs-2';

const BarChart = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    if (data.length > 0) {
      const ctx = document.getElementById('yourCanvasId').getContext('2d');
      new Chart(ctx, {
        type: 'bar',
        data: barChartData,
      });
    }
  }, [data, barChartData]);
  

  // Create a new array for labels and counts
  const labels = data.map((d) => d._id);
  const counts = data.map((d) => d.count);

  const barChartData = {
    labels: labels,
    datasets: [
      {
        label: 'Range',
        data: counts,
      },
    ],
  };

  const options = {
    scales: {
      x: {
        type: 'category', // or 'categoryAxis'
        labels: labels,
      },
      y: {
        beginAtZero: true, // adjust as needed
      },
    },
  };

  return <Bar data={barChartData} options={options} />;
};

export default BarChart;
