import React, { useEffect, useState, useMemo } from "react";
import axios from "axios";
import { useTable } from "react-table";
// import { COLUMNS } from "./columns";
import "./assets/BasicTable.css";

const BasicTable = () => {

    const columns = useMemo(
        () => [
            {
                Header:'ID',
                accesor:'id'
            },
            {
                Header:'Title',
                accesor:'title'
            },
            {
                Header:'Description',
                accesor:'description'
            },
            {
                Header:'Price',
                accesor:'price'
            },
            {
                Header:'Category',
                accesor:'category'
            },
            {
                Header:'Sold',
                accesor:'sold'
            },
            {
                Header:'Image',
                accesor:'image'
            },
        ],
        []
      );


  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          "http://localhost:3000/api/product/productTransactions",
          {
            params: {
              page: 1,
              limit: 10,
              search: " ",
            },
          }
        );

        setTransactions(response.data.transactions);
        setLoading(false);
      } catch (err) {
        setError(err.message);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

//   const columns = useMemo(() => COLUMNS, []);
  const data = useMemo(() => transactions, [transactions]);
  console.log("data=>>>", data);
  const {
    getTableProps, // table props from react-table
    getTableBodyProps, // table body props from react-table
    headerGroups, // headerGroups, if your table has groupings
    rows, // rows for the table based on the data passed
    prepareRow // Prepare the row (this function needs to be called for each row before getting the row props)
  } = useTable({
    columns,
    data
  });

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

//   const { getTableProps, getTableBodyProps, headerGroups,rows, prepareRow } =
//     tableInstance;
  //const{rows}=tableInstance.data
  console.log(">>", rows, tableInstance);

  return (
    <table {...getTableProps()}>
      <thead>
        {headerGroups.map((headerGroup) => (
          <tr {...headerGroup.getHeaderGroupProps()}>
            {headerGroup.headers.map((column) => (
              <th {...column.getHeaderProps()}>{column.render("Header")}</th>
            ))}
          </tr>
        ))}
      </thead>

      <tbody {...getTableBodyProps()}>
        {rows.map((row) => {
          prepareRow(row);
          return (
            <tr {...row.getRowProps()}>
              {row.cells.map((cell) => {
                console.log("cell==>>",cell);
                console.log("cellRebder==>>",cell.render('Cell'));
                
                return<><p>sdsd</p><td {...cell.getCellProps()}>{cell.render('Cell')}</td></>;
              })}
            </tr>
          );
        })}
      </tbody>

      {/* <tbody {...getTableBodyProps()}>
         {rows.map((row) => {
          prepareRow(row);
          return (
            <tr {...row.getRowProps()}>
              {row.cells.map((cell) => {
                return <td {...cell.getCellProps()}>{cell.render("cell")}</td>

              })}
            </tr>
          );
        })}
      </tbody> */}
    </table>
  );
};

